package note.deadPerson;import note.deadPerson.member.Grade;import note.deadPerson.member.Member;import note.deadPerson.member.MemberService;import note.deadPerson.member.MemberServiceImpl;import note.deadPerson.order.Order;import note.deadPerson.order.OrderService;import note.deadPerson.order.OrderServiceImpl;public class OrderApp {    public static void main(String[] args) {        MemberService memberService = new MemberServiceImpl();        OrderService orderService = new OrderServiceImpl();        Long memberId = 1L;        Member member = new Member(memberId, "memberA", Grade.VIP);        memberService.joinMember(member);        Order order = orderService.createOrder(memberId, "itemA", 10000);        System.out.println("Order : " + order);        System.out.println("DiscountPrice : " + order.calculatePrice());    }}