package note.deadPerson.discount;import note.deadPerson.member.Grade;import note.deadPerson.member.Member;public class FixDiscountPolicy implements DiscountPolicy {    private int discountFixAmount = 1000;    @Override    public int discount(Member member, int price) {        if (member.getGrdae() == Grade.VIP) {            return discountFixAmount;        } else {            return 0;        }    }}