package note.deadPerson.order;import note.deadPerson.discount.DiscountPolicy;import note.deadPerson.discount.FixDiscountPolicy;import note.deadPerson.member.Member;import note.deadPerson.member.MemberRepository;import note.deadPerson.member.MemoryMemberRepository;public class OrderServiceImpl implements OrderService {    private final MemberRepository memberRepository = new MemoryMemberRepository();    private final DiscountPolicy discountPolicy = new FixDiscountPolicy();    @Override    public Order createOrder(Long memberId, String itemName, int itemPrice) {        Member member = memberRepository.findById(memberId);        int discountPrice = discountPolicy.discount(member, itemPrice);        return new Order(memberId, itemName, itemPrice, discountPrice);    }}